import React, { useEffect, useState } from 'react';
import { fetchTasks, createTask, deleteTask } from '../services/Api';
import { useNavigate } from 'react-router-dom'; // Hook nawigacji
import '../styles/Dashboard.css'; // Import stylów

const Dashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [newDeadline, setNewDeadline] = useState(''); // Pole na datę deadline
  const navigate = useNavigate(); // Hook do nawigacji

  // Pobieranie zadań
  useEffect(() => {
    const loadTasks = async () => {
      const data = await fetchTasks();
      setTasks(data);
    };

    loadTasks();
  }, []);

  // Dodawanie nowego zadania
  const handleAddTask = async () => {
    if (newTask.trim() === '' || newDeadline === '') return;

    const result = await createTask({ title: newTask, date: newDeadline });
    if (result.success) {
      setTasks((prevTasks) => [...prevTasks, result.task]);
      setNewTask('');
      setNewDeadline('');
    }
  };

  // Zmiana statusu zadania
  const toggleTaskStatus = (id) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  // Usuwanie zadania
  const handleDeleteTask = async (id) => {
    const result = await deleteTask(id);
    if (result.success) {
      setTasks((prevTasks) => prevTasks.filter((task) => task.id !== id));
    }
  };

  // Nawigacja do szczegółów zadania
  const handleViewTask = (id) => {
    navigate(`/task/${id}`);
  };

  // Nawigacja do kalendarza
  const handleNavigateToCalendar = () => {
    navigate('/calendar');
  };

  // Nawigacja do profilu
  const handleNavigateToProfile = () => {
    navigate('/profile');
  };

  return (
    <div className="dashboard-container">
      <h1>Panel zadań</h1>

      {/* Przycisk do kalendarza */}
      <button className="calendar-button" onClick={handleNavigateToCalendar}>
        Przejdź do kalendarza
      </button>

      {/* Przycisk do profilu */}
      <button className="profile-button" onClick={handleNavigateToProfile}>
        Mój profil
      </button>

      {/* Dodawanie nowego zadania */}
      <div className="add-task">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Nowe zadanie"
        />
        <input
          type="date"
          value={newDeadline}
          onChange={(e) => setNewDeadline(e.target.value)}
        />
        <button onClick={handleAddTask}>Dodaj</button>
      </div>

      <ul className="task-list">
        {tasks.map((task) => (
          <li
            key={task.id}
            className={task.completed ? 'completed' : 'pending'}
          >
            {task.title} - {task.completed ? 'Zakończone' : 'W trakcie'}
            <br />
            Deadline: {new Date(task.date).toLocaleDateString()}
            <div className="task-actions">
              <button onClick={() => toggleTaskStatus(task.id)}>
                Zmień status
              </button>
              <button onClick={() => handleViewTask(task.id)}>
                Pokaż szczegóły
              </button>
              <button
                onClick={() => handleDeleteTask(task.id)}
                className="delete-task"
              >
                Usuń
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
