import React, { useState, useEffect } from 'react';
import { getUserDetails, updateUserDetails } from '../services/Api'; // Mockowane API
import { useNavigate } from 'react-router-dom'; // Import hooka nawigacji
import '../styles/Profile.css'; // Import stylów

const Profile = () => {
  const [name, setName] = useState('');
  const [position, setPosition] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Hook do nawigacji

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userData = await getUserDetails(); // Pobiera dane użytkownika
        setName(userData.name);
        setPosition(userData.position);
      } catch (err) {
        setError('Nie udało się załadować danych użytkownika.');
      }
    };

    fetchUserData();
  }, []);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const result = await updateUserDetails({ name, position });
      if (result.success) {
        setMessage('Dane zostały zaktualizowane pomyślnie.');
      } else {
        setError('Nie udało się zaktualizować danych.');
      }
    } catch (err) {
      setError('Wystąpił błąd podczas aktualizacji danych.');
    }
  };

  // Funkcja obsługująca powrót do dashboardu
  const handleBackToDashboard = () => {
    navigate('/dashboard'); // Przekierowanie na dashboard
  };

  return (
    <div className="profile-container">
      <h1>Profil użytkownika</h1>
      {message && <p className="success-message">{message}</p>}
      {error && <p className="error-message">{error}</p>}

      <form onSubmit={handleUpdate}>
        <label htmlFor="name">Imię</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Wprowadź swoje imię"
        />

        <label htmlFor="position">Stanowisko</label>
        <input
          type="text"
          id="position"
          value={position}
          onChange={(e) => setPosition(e.target.value)}
          placeholder="Wprowadź swoje stanowisko"
        />

        <button type="submit">Zapisz zmiany</button>
      </form>

      {/* Przycisk powrotu do dashboardu */}
      <button className="back-button" onClick={handleBackToDashboard}>
        Wróć do panelu
      </button>
    </div>
  );
};

export default Profile;
