import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchTaskDetails } from '../services/Api';
import '../styles/TaskDetails.css';

const TaskDetails = () => {
  const { id } = useParams();
  const [task, setTask] = useState(null);

  useEffect(() => {
    const getTaskDetails = async () => {
      const data = await fetchTaskDetails(id);
      setTask(data);
    };
    getTaskDetails();
  }, [id]);

  return (
    <div className="task-details-container">
      <h1>Szczegóły zadania</h1>
      {task ? (
        <>
          <p><strong>ID zadania:</strong> {task.id}</p>
          <p><strong>Tytuł:</strong> {task.title}</p>
          <p><strong>Opis:</strong> {task.description}</p>
          <p><strong>Status:</strong> {task.completed ? 'Zakończone' : 'W trakcie'}</p>
        </>
      ) : (
        <p>Wczytywanie...</p>
      )}
    </div>
  );
};

export default TaskDetails;
